<?php
  // open current directory 
  $rootDir = opendir(".");
  $dirArray = [];
  // get each entry
  while($element = readdir($rootDir)) {
    // exclude files and hidden folders
    if (is_dir($element) and (substr($element, 0, 1) != ".")){ 
      $dirArray[] = $element;
    }
  }
  // close directory
  closedir($rootDir);
  //  count elements in array
  $projectCount = count($dirArray) ;
  if ($projectCount > 0) {
    // sort 'em
    sort($dirArray);
  }
?>
<body>
  <div class="content">
    <span> <?php echo ($projectCount > 0)? $projectCount : "No"; ?> Projects</span>
    <ul class="sites">
      <?php if ($projectCount > 0) : ?>
        <?php foreach ($dirArray as $dir):?>
          <li><a href="<?php echo $dir ;?>"><?php echo $dir ;?></a></li>
        <?php endforeach; ?>
      <?php else: ?>
        <li><a href="#">Nothin here, start adding projects to your server.</a></li>
      <?php endif; ?>
    </ul>
  </div>
</body>
